import com.sun.xml.internal.bind.v2.runtime.reflect.opt.FieldAccessor_Double;import java.lang.reflect.Field;/** * @author: jt * @date: 2018-9-21 */public class DemoTest {    public static void main(String[] args) throws IllegalAccessException {        Cat cat = new Cat(1, "Tom", 12, "GZ");        Field[] field = Cat.class.getDeclaredFields();        for (Field field1 : field) {            field1.setAccessible(true);            System.out.print(field1.getName()+":");            Object o = field1.get(cat);            System.out.println(o+"");        }    }}